import React from "react";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import Popup from 'reactjs-popup';
import { Box, Button, Stack } from "@mui/material";
import MyButton from "../components/Buttons/MyButton.js";
import { useNavigate } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import Tab from '@mui/material/Tab';
import TabContext from '@mui/lab/TabContext';
import TabList from '@mui/lab/TabList';
import TabPanel from '@mui/lab/TabPanel';

const ProfilePage = () => {
    const navigateTo = useNavigate();
    const { handleSubmit } = useForm();
    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [uloga, setUloga] = useState('');
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [email, setEmail] = useState('');
    const [pbr, setPbr] = useState('');
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState('');
    const [oldPassword, setOldPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [repeatPassword, setRepeatPassword] = useState('');
    const [value, setValue] = useState('');

    const handleChange = (e) => {
        setPbr(e.target.value);
    };
    const handleOldPassword = (e) => {
        setOldPassword(e.target.value);

    };
    const handleNewPassword = (e) => {
        setNewPassword(e.target.value);

    };
    const handleRepeatPassword = (e) => {
        setRepeatPassword(e.target.value);

    };

    const handleName = (e) => {
        setName(e.target.value);

    };
    const handleSurname = (e) => {
        setSurname(e.target.value);
    };

    // Handling the email change
    const handleEmail = (e) => {
        setEmail(e.target.value);

    };

    const handleUloga = (a) => {
        if(a == 1) {
            setUloga('Admin');
        } else{
            setUloga('Korisnik');
        }
    };

    const changeName = async () => {
        await axios.post(`http://localhost:5000/changeName`,
            {
                name,
            }).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('ime');
            setTimeout(() => {setSuccess('');}, 1000);
            //setSuccess('ime');
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => {setError(false);}, 1000);
            setSuccess('');
            return err;
        });
    };

    const changeSurname = async () => {
        await axios.post(`http://localhost:5000/changeSurname`,
            {
                surname,
            }).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('prezime');
            setTimeout(() => {setSuccess('');}, 1000);
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => {setError(false);}, 1000);
            setSuccess('');
            return err;
        });
    };

    const changeEmail = async () => {
        await axios.post(`http://localhost:5000/changeEmail`,
            {
                email,
            }).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('email');
            setTimeout(() => {setSuccess('');}, 1000);
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => {setError(false);}, 1000);
            setSuccess('');
            return err;
        });
    };

    const changePbr = async () => {
        await axios.post(`http://localhost:5000/changePbr`,
            {
                pbr,
            }).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('mjesto stanovanja');
            setTimeout(() => {setSuccess('');}, 1000);
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => {setError(false);}, 1000);
            setSuccess('');
            return err;
        });
    };

    const getData = async () => {
        let dataa = (
            await axios.get('http://localhost:5000/session')
        ).data;
        //setValue(data[0]);
        setDataList(dataa);
        handleUloga(dataa.admin);
    };

    React.useEffect(() => {
        const getDat = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
            handleUloga(dataa.admin);
            setName(dataa.name);
            setSurname(dataa.surname);
            setEmail(dataa.email);
        };
        getDat().catch((err) => console.log(err));
    }, [refresh]);

    const successMessage = () => {
        return (
            <div
                className="success"
                style={{
                    display: (success != '') ? '' : 'none',
                }}>
                <p style={{ color: "green", fontSize: 20 }}>Uspješno ste promjenili {success}!</p>
            </div>
        );
    };

    
    // Showing error message if error is true
    const errorMessage = () => {
        return (
            <div
                className="error"
                style={{
                    display: error ? '' : 'none',
                }}>
                <p style={{ color: "red", fontSize: 20 }}>Vaša promjena nije uspjela</p>
            </div>
        );
    };
    const onSubmitting = async (c, e) => {
        e.preventDefault();
        //console.log(e);
        if ((newPassword !== repeatPassword)) {
            setError(true);
            setTimeout(() => {setError(false);}, 3000);
            return;
        }

        else {
            await axios.post(`http://localhost:5000/changePassword`,
                {
                    oldPassword,
                    newPassword
                }

            ).then((response) => {
                console.log(response.json);
                setError(false);
                setSuccess('lozinku');
                setTimeout(() => {setSuccess('');}, 3000);
                return response.data;
            }).catch((err) => {
                setError(true);
                setTimeout(() => {setError(false);}, 3000);
                setSuccess('');
                return err;
            });
        }
    };


    //if (!data) return <Navigate to='/' />;
    return (
        <div>
            <Header />
            <Box
                sx={{
                    display: 'flex',
                    alignItems: 'flex-start',
                    flexDirection: 'column',
                    p: 1,
                    m: 1,
                    borderRadius: 7,
                    border: '6px solid rgba(255, 165, 0, 0.5)',
                    position: 'absolute', left: '33%', top: '15%',
                    padding: '30px 30px 30px 30px',
                    borderColor: 'orange'
                }}>
                <Box sx={{ width: '100%', typography: 'body1' }}>
                    <TabContext value={value}>
                        <Box sx={{ borderBottom: 1, borderColor: 'divider' }}>
                            <TabList onChange={handleChange} aria-label="lab API tabs example">
                                <Tab label="Osobni podaci" value="1" style ={{color:"black"}}/>
                                <Tab label="Promijeni osobne podatke" value="2" style ={{color:"black"}} />
                                <Tab label="Promijeni lozinku" value="3" style ={{color:"black"}}/>
                            </TabList>
                        </Box>
                        
                        <TabPanel value="2">Item Two</TabPanel>
                        <TabPanel value="3">Item Three</TabPanel>
                    
                
                        <TabPanel value="1">
                            <Stack direction='column' justifyContent='center' alignItems='center' spacing='1rem'>
                                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                                    <p> Korisničko ime: {data.username}</p>
                                </Stack>
                                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                                    <p>Ime: {data.name} </p>
                                </Stack>
                                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                                    <p>Prezime: {data.surname} </p>
                                </Stack>
                                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                                    <p>Email adresa: {data.email}</p>
                                </Stack>
                                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                                    <p>Uloga: {uloga}</p>
                                </Stack>
                            </Stack>

                        </TabPanel>
                    </TabContext>
                </Box>

                
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <p>Grad: </p>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <Popup trigger=
                        {<MyButton className="butonn"> Uredi podatke </MyButton>}
                    modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '70px',

                                }}>
                                    <div className='content'>
                                        Ispunite polja koja želite mjenjati, te stisnite zelenu kvačicu
                                        <br />
                                        pored njih. Pri završetku stisnite gumb <h style={{ color: 'orange' }}>Gotovo</h>,


                                    </div>
                                    <br />
                                    <form>
                                        {/* Labels and inputs for form data */}
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                            <label className="label">Ime:  </label>

                                            <input onChange={handleName} className="input"
                                                type="text" name='name' id="name" value={name} placeholder="Name" required />
                                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={function(){changeName(); getData();}} />
                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                                            <label className="label">Prezime:   </label>

                                            <input onChange={handleSurname} className="input"
                                                value={surname} type="text" name='surname' id="surname" placeholder='Last name' required />
                                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={function(){changeSurname(); getData();}} />
                                        </Stack>

                                        <br />

                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >
                                            <label className="label">Email:</label>
                                            <input onChange={handleEmail} className="input"
                                                value={email} type="email" name='email' id="email" placeholder='Email' required />
                                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={function(){changeEmail(); getData();}} />
                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='flex-start' alignItems='center' spacing='11.4rem' >
                                            <label className="label">Grad</label>
                                            <select name='pbr' title='pbr' id="pbr" value={pbr} onChange={handleChange}>
                                                <option value="12321">12321</option>
                                                <option value="12343">12343</option>
                                            </select>
                                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={function(){changePbr(); getData();}} />
                                        </Stack>
                                        <br />
                                    </form>
                                    <div>
                                        <MyButton onClick=
                                            {() => close()}>
                                            Gotovo
                                        </MyButton>
                                    </div>
                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '10%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>

                    <Popup trigger=
                        {<MyButton className="butonn"> Promijeni lozinku </MyButton>}
                    modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '125px 70px',

                                }}>
                                    <div className='content'>
                                        
                                    </div>
                                    <br />
                                    <form onSubmit={handleSubmit(onSubmitting)}>
                                        {/* Labels and inputs for form data */}
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                            <label className="label">Stara lozinka:  </label>

                                            <input onChange={handleOldPassword} className="input"
                                                type="password" name='oldPassword' id="oldPassword" value={oldPassword} placeholder="" required />

                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                                            <label className="label">Nova lozinka:   </label>

                                            <input onChange={handleNewPassword} className="input"
                                                value={newPassword} type="password" name='newPassword' id="newPassword" placeholder='' required />

                                        </Stack>

                                        <br />

                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >
                                            <label className="label">Ponovite novu lozinku:</label>
                                            <input onChange={handleRepeatPassword} className="input"
                                                value={repeatPassword} type="password" name='repeatPassword' id="repeatPassword" required />
                                        </Stack>

                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >
                                            <MyButton type="submit">
                                                Promijeni lozinku
                                            </MyButton>
                                            <MyButton onClick=
                                                {() => close()}>
                                                Zatvori
                                            </MyButton>
                                        </Stack>
                                    </form>
                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '10%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>
                </Stack>




            </Box>

        </div>
    );
};

export default ProfilePage;
