import { useState } from 'react';
import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useForm } from 'react-hook-form';

export default function Form() {

    let axiosConfig = {
        headers: {
            'Content-Type': 'application/json;charset=UTF-8',
            "Access-Control-Allow-Origin": "*",
        }
    };
    // States for registration
    const [username, setUsername] = useState('');
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [repeatPassword, setRepeatPassword] = useState('');
    const navigateTo = useNavigate();
    const { handleSubmit } = useForm();

    // States for checking the errors
    const [submitted, setSubmitted] = useState(false);
    const [error, setError] = useState(false);
    const [selectedItem, setSelectedItem] = useState("");
    const handleChange = (e) => {
        setSelectedItem(e.target.value);
    };

    // Handling the name change
    const handleUsername = (e) => {
        setUsername(e.target.value);
        setSubmitted(false);
    };

    const handleName = (e) => {
        setName(e.target.value);
        setSubmitted(false);
    };
    const handleSurname = (e) => {
        setSurname(e.target.value);
        setSubmitted(false);
    };

    // Handling the email change
    const handleEmail = (e) => {
        setEmail(e.target.value);
        setSubmitted(false);
    };

    const handlePassword = (e) => {
        setPassword(e.target.value);
        setSubmitted(false);
    };
    const handleRepeatPassword = (e) => {
        setRepeatPassword(e.target.value);
        setSubmitted(false);
    };

    // Handling the form submission
    const onSubmitting = async (form, e) => {
        e.preventDefault();
        console.log(e);
        if ((name === '' || username === '' || surname === '' || email === '' || password === '') || (password !== repeatPassword)) {
            setError(true);
            return;
        }

        else {
            await axios.post(`${import.meta.env.VITE_API}/register`, {
                body: JSON.stringify({
                    name: name, username: username, surname: surname, password: password, pbr: selectedItem, email: email
                }),
                method: 'POST',
                headers: {
                    "Content-type": "application/json; charset=UTF-8"
                }
            }).then((response) => {
                console.log(response.json); 
                navigateTo('/');
                setError(false);
                setSubmitted(true);
                return response.data;
            }).catch( (err) => {
                setError(true);
                return err;
            });
        }
    };
    

    // Showing success message
    const successMessage = () => {
        return (
            <div
                className="success"
                style={{
                    display: submitted ? '' : 'none',
                }}>
                <h1> User {name} successfully registered!!</h1>
            </div>
        );
    };


    // Showing error message if error is true
    const errorMessage = () => {
        return (
            <div
                className="error"
                style={{
                    display: error ? '' : 'none',
                }}>
                <p style={{ color: "red" }}>Nešto ne valja! Pokušajte ponovno</p>
            </div>
        );
    };

    return (
        <div className='form'>
            <div className="p-3 my-5 d-flex flex-column w-50" direction="row" style={{
                position: 'absolute', left: '50%', top: '55%',
                transform: 'translate(-50%, -50%)',
                border: '10px solid rgba(255, 165, 0, 0.5)',
                padding: '20px'
            }}>
                <p style={{ fontSize: 15 }}> Registracija</p>

                {/* Calling to the methods */}

                <form onSubmit={handleSubmit(onSubmitting)}>
                    {/* Labels and inputs for form data */}
                    <label className="label">Korisničko ime</label>
                    <br />
                    <input onChange={handleUsername} className="input"
                        value={username} type="text" name='username' id="username" placeholder='Username' required />
                    <br /> <br />
                    <label className="label">Ime</label>
                    <br />
                    <input onChange={handleName} className="input"
                        value={name} type="text" name='name' id="name" placeholder='Name' required />
                    <br /> <br />
                    <label className="label">Prezime</label>
                    <br />
                    <input onChange={handleSurname} className="input"
                        value={surname} type="text" name='surname' id="surname" placeholder='Last name' required />
                    <br /> <br />
                    <label className="label">Email</label>
                    <br />
                    <input onChange={handleEmail} className="input"
                        value={email} type="email" name='email' id="email" placeholder='Email' required />
                    <br /><br />
                    <label className="label">Lozinka</label>
                    <br />
                    <input onChange={handlePassword} className="input"
                        value={password} type="password" name='password' id="password" placeholder='Password' required />
                    <br /><br />
                    <label className="label">Ponovite lozinku</label>
                    <br />
                    <input onChange={handleRepeatPassword} className="input"
                        value={repeatPassword} type="password" name='repeatPassword' id="repeatPassword" placeholder='Repeat password' required />
                    <br /><br />
                    <label className="label">Grad</label>
                    <br />
                    <select name='pbr' title='pbr' id="pbr" value={selectedItem} onChange={handleChange}>
                        <option value="Counter">Counter</option>
                        <option value="useEffect">useEffect</option>
                    </select>
                    <br /><br />
                    <button className="btn" type="submit">
                        Submit
                    </button>
                </form>
            </div>
            <div className="messages" style={{
                position: 'absolute', left: '50%', top: '12%',
                transform: 'translate(-50%, -50%)'
            }}>
                {errorMessage()}
                {successMessage()}
            </div>
        </div>
    );
}
