import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { Controller, useForm } from 'react-hook-form';
import BasicDateTimePicker from "../components/DateTimePicker.js";
import ImageUploading from "react-images-uploading";
import { Box, Button, Stack } from "@mui/material";

const CreateQuiz = () => {

    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [dateAndTimeOpen, setDateAndTimeOpen] = useState(Date.now());
    const [dateAndTimeClose, setDateAndTimeClose] = useState(Date.now());
    const [duration, setDuration] = useState('');
    const [reviewDuration, setReviewDuration] = useState('');
    const [images, setImages] = React.useState();
    const maxNumber = 1;

    const handleName = (e) => {
        setName(e.target.value);
    };
    const handleDescription = (e) => {
        setDescription(e.target.value);
    };
    const handleDuration = (e) => {
        setDuration(e.target.value);
    };
    const handleReviewDuration = (e) => {
        setReviewDuration(e.target.value);
    };

    function UploadImage() {
        const onChange = (imageList, addUpdateIndex) => {
            // data for submit
            console.log(imageList, addUpdateIndex);
            setImages(imageList);
        };

        return (
            <div className="App">
                <ImageUploading
                    multiple
                    value={images}
                    onChange={onChange}
                    maxNumber={maxNumber}
                    dataURLKey="data_url"
                    acceptType={["jpg"]}
                >
                    {({
                        imageList,
                        onImageUpload,
                        onImageRemoveAll,
                        onImageUpdate,
                        onImageRemove,
                        isDragging,
                        dragProps
                    }) => (
                        // write your building UI
                        <div className="upload__image-wrapper">
                            <button
                                style={isDragging ? { color: "red" } : null}
                                onClick={onImageUpload}
                                {...dragProps}
                            >
                                Click or Drop here
                            </button>
                            &nbsp;
                            {imageList.map((image, index) => (
                                <div key={index} className="image-item">
                                    <img src={image.data_url} alt="" width="100" />
                                    <div className="image-item__btn-wrapper">
                                        <button onClick={() => onImageUpdate(index)}>Update</button>
                                        <button onClick={() => onImageRemove(index)}>Remove</button>
                                    </div>
                                </div>
                            ))}
                        </div>
                    )}
                </ImageUploading>
            </div>
        );
    }



    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        getData().catch((err) => console.log(err));
    }, [refresh]);

    return (
        <div>
            <Header />
            <div className='form'>
                <div className="p-3 my-5 d-flex flex-column w-50" direction="row" style={{
                    position: 'absolute', left: '50%', top: '55%',
                    transform: 'translate(-50%, -50%)',
                    border: '10px solid rgba(255, 165, 0, 0.5)',
                    padding: '20px'
                }}>
                    <h1> Kreiraj novi kviz</h1>

                    {/* Calling to the methods */}
                    <form>
                        {/* Labels and inputs for form data */}
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='8px' >
                            <label className="label">Naziv kviza  </label>

                            <input onChange={handleName} className="input"
                                type="text" name='name' id="name" value={name} placeholder="Zastave Europe" required />
                        </Stack>
                        <br />
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                            <label className="label">Opis kviza:  </label>

                            <textarea onChange={handleDescription} className="input"
                                type="textarea" name='description' id="description" value={description} placeholder="Vrlo poučan i edukativan kviz za sve uzraste" required />
                        </Stack>
                        <br />
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                            <label className="label">Broj sekundi trajanja:   </label>

                            <input onChange={handleDuration} className="input"
                                value={duration} type="text" name='duration' id="duration" placeholder='60' required />
                        </Stack>

                        <br />

                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='4.2rem' >
                            <label className="label">Broj sekundi trajanja pregleda:</label>
                            <input onChange={handleReviewDuration} className="input"
                                value={reviewDuration} type="text" name='reviewDuration' id="reviewDuration" placeholder='100' required />

                        </Stack>
                        <br />
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='4.2rem' >
                            <Stack direction='column' justifyContent='space-between' alignItems='center' spacing='1rem' >
                                <label className="label">Datum i vrijeme otvaranja kviza</label>
                                <BasicDateTimePicker
                                    value={dateAndTimeOpen}
                                    onChange={(newValue) => setDateAndTimeOpen(newValue)}
                                    name='dateAndTimeOpen'
                                    required
                                />
                            </Stack>
                            <Stack direction='column' justifyContent='space-between' alignItems='center' spacing='1rem' >
                                <label className="label">Datum i vrijeme zatvaranja kviza</label>
                                <BasicDateTimePicker
                                    value={dateAndTimeClose}
                                    onChange={(newValue) => setDateAndTimeClose(newValue)}
                                    name='dateAndTimeClose'
                                    required
                                />
                            </Stack>
                        </Stack>
                        <br />
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='4.2rem' >
                            <label className="label">Učitajte sliku</label>
                            <br />
                            <UploadImage />
                        </Stack>
                        <br />
                        <button className="btn" type="submit">
                            Submit
                        </button>

                        <br />
                    </form>

                </div>
                <div className="messages" style={{
                    position: 'absolute', left: '50%', top: '20%',
                    transform: 'translate(-50%, -50%)'
                }}>

                </div>
            </div>
        </div>
    );
};

export default CreateQuiz;
