import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { Stack } from "@mui/material";
import MyButton from "../components/Buttons/MyButton.js";
import MySmallButton from "../components/Buttons/MySmallButton.js";
import { useNavigate, useParams } from "react-router-dom";
import { Controller, useForm } from 'react-hook-form';
import Popup from 'reactjs-popup';

const ManageQuestions = () => {

    const navigateTo = useNavigate();
    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [question1, setQuestion1] = useState('');
    const [answer1, setAnswer1] = useState('');
    const [incorrectAnswer, setIncorrectAnswer] = useState('');
    const [question2, setQuestion2] = useState('');
    const [answer2, setAnswer2] = useState('');
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState('');
    const [question1Get, setQuestion1Get] = useState('');
    const [answer1Get, setAnswer1Get] = useState('');
    const [slagalica, setSlagalica] = useState('');
    const [slagalicaL, setSlagalicaL] = useState('');
    const [slagalicaD, setSlagalicaD] = useState('');
    const [rememberedQuestion, setRememberedQuestion] = useState('');
    const [rememberedAnswer, setRememberedAnswer] = useState('');
    const [pairs, setPairs] = useState('');
    const { handleSubmit } = useForm();
    const { sifKviz } = useParams();
    const [questions, setQuestions] = useState([]);
    const [numberOfSearch, setNumberOfSearch] = useState(10);
    const [quiz, setQuiz] = useState('');
    const [numberOfSearch2, setNumberOfSearch2] = useState(7);

    const handleQuestion1 = (e) => {
        setQuestion1(e.target.value);
    };

    const handleSearch = () => {
        setNumberOfSearch(numberOfSearch + 5);
    };

    const handleNumberOfSearch = () => {
        setNumberOfSearch2(numberOfSearch2 + 5);
    };

    const handleAnswer1 = (e) => {
        setAnswer1(e.target.value);
    };


    const handleQuestion2 = (e) => {
        setQuestion2(e.target.value);
    };

    const handleAnswer2 = (e) => {
        setAnswer2(e.target.value);
    };

    const handleIncorrectAnswer = (e) => {
        setIncorrectAnswer(e.target.value);
    };

    const handleSlagalicaPitanje = (e) => {
        setSlagalica(e.target.value);
    };
    const handleSlagalicaL = (e) => {
        setSlagalicaL(e.target.value);
    };
    const handleSlagalicaD = (e) => {
        setSlagalicaD(e.target.value);
    };

    const get1 = async (sifPitanje) => {
        const getQuestion = async () => {
            let data2 = (
                await axios.get('http://localhost:5000/admin/quiz/getQuestion1/' + sifPitanje)
            ).data;
            //setValue(data[0]);
            setQuestion1Get(data2.tekstPitanja);
            setAnswer1Get(data2.odgovor);
        };
        getQuestion().catch((err) => console.log(err));
    };

    const submitCorrectAnswer = async (c, e) => {
        e.preventDefault();
        await axios.post(`http://localhost:5000/admin/quiz/createQuestion1/` + sifKviz + '/' + 2,
            {
                question1: question2,
                answer1: answer2
            }

        ).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('pitanje');
            setTimeout(() => { setSuccess(''); }, 3000);
            //setValue(data[0]);
            setRememberedQuestion(question2);
            setRememberedAnswer(answer2);
            setQuestionsHelp();

            renderSearchTable();
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => { setError(false); }, 3000);
            setSuccess('');
            return err;
        });


    };


    const onSubmitting = async (c, e) => {
        e.preventDefault();
        await axios.post(`http://localhost:5000/admin/quiz/createQuestion1/` + sifKviz + '/' + 1,
            {
                question1,
                answer1
            }

        ).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('pitanje');
            setTimeout(() => { setSuccess(''); }, 3000);
            //setValue(data[0]);
            setQuestionsHelp();

            renderSearchTable();
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => { setError(false); }, 3000);
            setSuccess('');
            return err;
        });


    };

    const submitSlagalica = async (c, e) => {
        e.preventDefault();
        await axios.post(`http://localhost:5000/admin/quiz/createSlagalica/` + sifKviz + '/' + 3,
            {
                question: slagalica,
                pair1: slagalicaL,
                pair2: slagalicaD
            }

        ).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('pitanje');
            setTimeout(() => { setSuccess(''); }, 3000);
            //setValue(data[0]);
            setQuestionsHelp();

            renderSearchTable();
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => { setError(false); }, 3000);
            setSuccess('');
            return err;
        });


    };

    const addIncorrect = async (c, e) => {
        e.preventDefault();
        await axios.post(`http://localhost:5000/admin/quiz/createIncorrectAnswer/` + sifKviz + '/' + 2,
            {
                question: rememberedQuestion,
                correctAnswer: rememberedAnswer,
                incorrectAnswer: incorrectAnswer
            }

        ).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('pogreÅ¡an odgovor');
            setTimeout(() => { setSuccess(''); }, 3000);
            //setValue(data[0]);
            setQuestionsHelp();

            renderSearchTable();
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => { setError(false); }, 3000);
            setSuccess('');
            return err;
        });


    };

    const setQuestionsHelp = async () => {
        let data2 = (
            await axios.get('http://localhost:5000/admin/quiz/getQuestions/' + sifKviz)
        ).data;
        setQuestions(data2);
        renderSearchTable();
    };

    const successMessage = () => {
        return (
            <div
                className="success"
                style={{
                    display: (success != '') ? '' : 'none',
                }}>
                <p style={{ color: "green", fontSize: 20 }}>UspjeÅ¡no ste dodali {success}</p>
            </div>
        );
    };


    // Showing error message if error is true
    const errorMessage = () => {
        return (
            <div
                className="error"
                style={{
                    display: error ? '' : 'none',
                }}>
                <p style={{ color: "red", fontSize: 20 }}>Nije uspjelo, pokuÅ¡ajte ponovno</p>
            </div>
        );
    };

    const renderQuestionTable = () => {
        var i = 0;
        const num = numberOfSearch;
        if (searchData) {
            return searchData.map(kviz => {
                while (i < num) {
                    i = i + 1;
                    return (
                        <tr key={kviz.sifKviz} style={{ height: '40px', textAlign: 'center' }}>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={kviz.image} alt="" width="100" /></td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.name}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid', maxWidth: '300px', overflowX: 'auto' }}>{kviz.description}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.nazivKategorije}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.availableFrom}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.availableTo}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.durationSec}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.reviewDurationSec}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://www.vagaservis.hr/images/upitnik.jpg'} style={{ height: '50px' }} onClick={function () { navigateTo('/admin/manageQuestions/' + kviz.sifKviz); }}></img></td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://cdn.dribbble.com/users/3293/screenshots/2591498/screen_shot_2016-03-15_at_12.51.52_pm.png'} style={{ position: 'relative', height: '50px', width: '50px' }} onClick={function () { navigateTo('/admin/manageQuizes/' + kviz.sifKviz); }} /></td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://cdn-icons-png.flaticon.com/512/5499/5499405.png'} style={{ position: 'relative', left: '40%', height: '30px', width: '30px' }} onClick={function () { removeQuiz(kviz.sifKviz); }} /></td>

                        </tr>
                    );
                }
            });
        }
        else {
            return (
                <p>Ne postoji takav korisnik!</p>
            );
        }

    };

    const renderSearchTable = () => {
        var i = 0;
        const num = numberOfSearch;
        if (questions) {
            return questions.map(q => {
                while (i < num) {
                    i = i + 1;
                    return (
                        <tr key={q.sifPitanje} style={{ height: '40px', textAlign: 'center' }}>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{i}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid', maxWidth: '500px', overflowX: 'auto' }}>{q.tekstPitanja}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid', borderRight: '1px solid #eeeee4', }}>{q.nazivTip}</td>
                            {q.sifTip === 1 &&
                                <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://www.backgroundsy.com/file/blue-info-icon.jpg'} style={{ position: 'relative', height: '35px', width: '45px' }} onClick={function () { navigateTo('/admin/manageQuestion/input/' + q.sifPitanje); }} /></td>
                            }
                            {q.sifTip == 2 &&
                                <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://www.backgroundsy.com/file/blue-info-icon.jpg'} style={{ position: 'relative', height: '35px', width: '45px' }} onClick={function () { navigateTo('/admin/manageQuestion/multipleChoice/' + q.sifPitanje); }} /></td>}
                            {q.sifTip == 3 &&
                                <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://www.backgroundsy.com/file/blue-info-icon.jpg'} style={{ position: 'relative', height: '35px', width: '45px' }} onClick={function () { navigateTo('/admin/manageQuestion/connectPairs/' + q.sifPitanje); }} /></td>}


                        </tr>
                    );
                }
            });
        }
        else {
            return (
                <p>Ne postoji takav korisnik!</p>
            );
        }

    };

    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuestions = async () => {
            let data2 = (
                await axios.get('http://localhost:5000/admin/quiz/getQuestions/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setQuestions(data2);
        };
        const getQuizInfo = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/admin/getQuiz/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setQuiz(dataa);
        };
        getData().catch((err) => console.log(err));
        getQuestions().catch((err) => console.log(err));
        getQuizInfo().catch((err) => console.log(err));

    }, [refresh]);


    return (
        <div>
            <Header />
            <Stack direction='column' justifyContent='center' alignItems='top' spacing='1rem'>
                <h1 style={{ alignSelf: 'center', marginBlockStart: '50px' }}> Upravljaj pitanjima za kviz {quiz.name}</h1>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem' style={{ marginInline: '200px', alignSelf: 'center' }}>
                    <Popup trigger=
                        {<MyButton className="butonn"> Dodaj novo pitanje slobodnog odgovora </MyButton>}
                        modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '125px 70px',

                                }}>
                                    <div className='content'>
                                        <p>Dodaj novo pitanje slobodnog odgovora!</p>
                                    </div>
                                    <br />
                                    <form onSubmit={handleSubmit(onSubmitting)} >
                                        {/* Labels and inputs for form data */}
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                            <label className="label">Tekst pitanja:  </label>

                                            <textarea style={{ height: '40px', width: '400px' }} onChange={handleQuestion1} className="input"
                                                type="textarea" name='question1' id="question1" value={question1} placeholder="Koji je glavni grad Meksika?" required />

                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                                            <label className="label">Odgovor:   </label>

                                            <textarea style={{ height: '40px', width: '400px' }} onChange={handleAnswer1} className="input"
                                                value={answer1} type="textarea" name='answer1' id="answer1" placeholder='Mexico City' required />

                                        </Stack>

                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >
                                            <MyButton type="submit">
                                                Spremi pitanje
                                            </MyButton>
                                            <MyButton onClick=
                                                {() => close()}>
                                                Zatvori
                                            </MyButton>


                                        </Stack>
                                    </form>
                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '20%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>
                    <Popup trigger=
                        {<MyButton className="butonn"> Dodaj novo slagalica pitanje </MyButton>}
                        modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '50px 60px',

                                }}>
                                    <div className='content'>
                                        <p>Dodaj novo slagalica pitanje!</p>
                                    </div>
                                    <br />
                                    <Stack direction='column' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                        <form onSubmit={handleSubmit(submitSlagalica)}>
                                            {/* Labels and inputs for form data */}
                                            <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                                <label className="label">Tekst pitanja:  </label>

                                                <textarea style={{ height: '40px', width: '400px' }} onChange={handleSlagalicaPitanje} className="input"
                                                    type="text" name='slagalica' id="slagalica" value={slagalica} placeholder="PoveÅ¾i drÅ¾avu i poznatu liÄnost" required />

                                            </Stack>
                                            <br />
                                            <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                                <label className="label">Pojam:  </label>

                                                <textarea style={{ height: '40px', width: '400px' }} onChange={handleSlagalicaL} className="input"
                                                    type="text" name='slagalicaL' id="slagalicaL" value={slagalicaL} placeholder="Portugal" required />
                                            </Stack>
                                            <br />
                                            <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                                <label className="label">Dodaj par navedenom pojmu:  </label>

                                                <textarea style={{ height: '40px', width: '400px' }} onChange={handleSlagalicaD} className="input"
                                                    type="text" name='slagalicaD' id="slagalicaD" value={slagalicaD} placeholder="Cristiano Ronaldo" required />
                                            </Stack>
                                            <br />
                                            <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                                <MyButton type="submit">
                                                    Spremi par
                                                </MyButton>
                                                <MyButton onClick=
                                                    {() => close()}>
                                                    Zatvori
                                                </MyButton>

                                            </Stack>

                                        </form>

                                        {/* Labels and inputs for form data */}



                                        <br />




                                    </Stack>

                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '20%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>
                    <Popup trigger=
                        {<MyButton className="butonn"> Dodaj novo pitanje sa ponuÄenim odgovorima </MyButton>}
                        modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '70px 50px',

                                }}>
                                    <div className='content'>
                                        <p>Dodaj novo pitanje sa ponuÄenim odgovorima!</p>
                                    </div>
                                    <br />
                                    <form onSubmit={handleSubmit(submitCorrectAnswer)}>
                                        {/* Labels and inputs for form data */}
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                            <label className="label">Tekst pitanja:  </label>

                                            <textarea style={{ height: '40px', width: '400px' }} onChange={handleQuestion2} className="input"
                                                type="text" name='question2' id="question2" value={question2} placeholder="Koje godine je palo Zapadno Rimsko Carstvo?" required />

                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                                            <label className="label">ToÄan odgovor:   </label>

                                            <textarea style={{ height: '40px', width: '400px' }} onChange={handleAnswer2} className="input"
                                                value={answer2} type="text" name='answer2' id="answer2" placeholder='476. godine' required />

                                        </Stack>
                                        <br />
                                        <MyButton type="submit">
                                            Spremi toÄan odgovor!
                                        </MyButton>
                                    </form>
                                    <Stack direction='column' justifyContent='center' alignItems='top' spacing='1rem' style={{ marginInline: '200px', alignSelf: 'center' }}>
                                        <p>Dodaj netoÄne odgovore:</p>
                                        <form onSubmit={handleSubmit(addIncorrect)}>

                                            <Stack direction='row' justifyContent='left' alignItems='center' spacing='5rem' >
                                                <textarea style={{ height: '40px', width: '400px' }} onChange={handleIncorrectAnswer} className="input"
                                                    type="text" name='incorrectAnswer' id="incorrectAnswer" value={incorrectAnswer} placeholder="2028. godine" />
                                                <MyButton type="submit">Dodaj</MyButton>
                                            </Stack>
                                        </form>

                                    </Stack>
                                    <br />
                                    <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >

                                        <MyButton onClick=
                                            {() => close()}>
                                            Zatvori
                                        </MyButton>


                                    </Stack>

                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '10%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>
                    <Popup trigger=
                        {<MyButton className="butonn"> Dodaj postojeÄe pitanje </MyButton>}
                        modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '60px 60px',

                                }}>
                                    <div>
                                        <Stack direction='column' justifyContent='center' alignItems='top' spacing='1rem' style={{ marginInline: '50px' }}>
                                            <form>
                                                <br />
                                                <p>PretraÅ¾i kvizove</p>
                                                <Stack direction='row' justifyContent='left' alignItems='center' spacing='5rem' >
                                                    <input style={{ height: '50px', width: '400px' }} onChange={handleQuiz} className="input"
                                                        type="text" name='searchQuiz' id="searchQuiz" value={searchQuiz} placeholder="UpiÅ¡ite ime:" />

                                                </Stack>
                                            </form>
                                            {searchData &&
                                                < table id="users">
                                                    <thead>
                                                        <tr style={{
                                                            height: '40px',
                                                            padding: '12px 0px 12px 0px',
                                                            textAlign: 'center',
                                                            backgroundColor: 'rgba(255, 165, 0, 0.5)',


                                                        }}>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Slika</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Naziv kviza</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Kratki opis</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Kategorija</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Dostupan od</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Dostupan do</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Trajanje u sekundama</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Trajanje pregleda u sekundama</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Upravljaj pitanjima</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Uredi kviz</th>
                                                            <th style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>IzbriÅ¡i kviz</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>{renderSearchTable()}</tbody>
                                                    <MyButton onClick={function () { handleSearch(); }}>Show more!</MyButton>
                                                </table>
                                            }

                                        </Stack>
                                    </div>
                                </div>
                            )
                        }
                    </Popup>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem' style={{ marginInline: '20px', alignSelf: 'center' }}>
                    {questions &&
                        < table id="users">
                            <thead>
                                <tr style={{
                                    height: '40px',
                                    padding: '12px 0px 12px 0px',
                                    textAlign: 'center',
                                    backgroundColor: 'rgba(255, 165, 0, 0.5)',


                                }}>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Broj</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Tekst pitanja</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px', width: '200px' }}>Tip pitanja</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>ViÅ¡e informacija</th>

                                </tr>
                            </thead>
                            <tbody>{renderSearchTable()}</tbody>
                            <MyButton onClick={function () { handleNumberOfSearch(); }}>Show more!</MyButton>
                        </table>
                    }
                </Stack>

            </Stack >
        </div >
    );
};

export default ManageQuestions;
