import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { useNavigate, useParams } from "react-router-dom";
import { Stack, Box } from "@mui/material";
import { EditText, EditTextarea } from 'react-edit-text';
import MyButton from "../components/Buttons/MyButton.js";
import MySmallButton from "../components/Buttons/MySmallButton.js";
import ImageUploading from "react-images-uploading";

const ManageSpecificQuiz = () => {

    const [error, setError] = useState(false);
    const [name, setName] = useState('');
    const [success, setSuccess] = useState('');
    const [data, setDataList] = useState('');
    const [refresh, setRefresh] = useState(false);
    const [kviz, setKviz] = useState('');
    const { sifKviz } = useParams();
    const [image, setImages] = React.useState();
    const [prvaSlika, setPrvaSlika] = useState(true);
    const maxNumber = 1;

    const handleName = (e) => {
        setName(e.target.value);
    }
    const changeName = async () => {
        await axios.post(`http://localhost:5000/admin/quiz/changeName/` + sifKviz,
            {
                name,
            }).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('ime');
            setTimeout(() => { setSuccess(''); }, 1000);
            //setSuccess('ime');
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => { setError(false); }, 1000);
            setSuccess('');
            return err;
        });
    };

    const successMessage = () => {
        return (
            <div
                className="success"
                style={{
                    display: (success != '') ? '' : 'none',
                }}>
                <p style={{ color: "green", fontSize: 20 }}>Uspješno ste promjenili {success}!</p>
            </div>
        );
    };


    // Showing error message if error is true
    const errorMessage = () => {
        return (
            <div
                className="error"
                style={{
                    display: error ? '' : 'none',
                }}>
                <p style={{ color: "red", fontSize: 20 }}>Vaša promjena nije uspjela</p>
            </div>
        );
    };

    function UploadImage() {
        const onChange = (imageList, addUpdateIndex) => {
            // data for submit
            setPrvaSlika(false);
            console.log(imageList, addUpdateIndex);
            setImages(imageList);
        };

        return (
            <div className="App">
                <ImageUploading
                    multiple
                    value={image}
                    onChange={onChange}
                    maxNumber={maxNumber}
                    dataURLKey="data_url"
                    acceptType={["jpg"]}
                >
                    {({
                        imageList,
                        onImageUpload,
                        onImageRemoveAll,
                        onImageUpdate,
                        onImageRemove,
                        isDragging,
                        dragProps
                    }) => (
                        // write your building UI
                        <div className="upload__image-wrapper">

                            &nbsp;
                            {imageList.map((image, index) => (
                                <div key={index} className="image-item">
                                    <Stack direction='column' justifyContent='space-between' alignItems='center' spacing='1rem'>
                                        {prvaSlika &&
                                            <img src={image} alt="" width="300" />
                                        }
                                        {!prvaSlika &&
                                            <img src={image.data_url} alt="" width="300" />
                                        }
                                        <div className="image-item__btn-wrapper">

                                            <MyButton type="button" onClick={function () { onImageUpdate(index); }}>Update</MyButton>



                                        </div>
                                    </Stack>
                                </div>
                            ))}
                        </div>
                    )}
                </ImageUploading>
            </div>
        );
    }


    React.useEffect(() => {

        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuiz = async () => {
            let data2 = (await (axios.get('http://localhost:5000/admin/getQuiz/' + sifKviz))).data;
            setKviz(data2);
            setImages([data2.image]);
        };
        getData().catch((err) => console.log(err));
        getQuiz().catch((err) => console.log(err));



    }, [refresh]);

    return (
        <div>
            <Header />
            <Box
                sx={{
                    display: 'flex',
                    alignItems: 'flex-start',
                    flexDirection: 'column',
                    p: 1,
                    m: 1,
                    borderRadius: 7,
                    border: '6px solid rgba(255, 165, 0, 0.5)',
                    position: 'absolute', left: '20%', top: '15%',
                    padding: '30px 30px 30px 30px',
                    borderColor: 'orange'
                }}>
                <Stack direction='row' justifyContent='flex-start' alignItems='center' spacing='1rem'>
                    <UploadImage />
                    <Stack direction='column' justifyContent='flex-start' alignItems='flex-start' spacing='0rem'>
                        <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                            <p > Naziv kviza: </p>
                            <EditText onChange={handleName} value= {name} defaultValue={kviz.name}></EditText>
                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={function () { changeName(); }} />
                        </Stack>
                        <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                            <p> Kratki opis kviza: </p>
                            <EditText style={{ maxWidth: '300px', overflowX: 'auto' }} defaultValue={kviz.description} />
                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} />
                        </Stack>
                        <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                            <p>Kategorija: </p>
                            <EditText defaultValue={kviz.nazivKategorije} />
                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} />
                        </Stack>
                        <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                            <p>Trajanje u sekundama: </p>
                            <EditText defaultValue={kviz.durationSec} />
                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} />
                        </Stack>
                        <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                            <p>Trajanje pregleda u sekundama: </p>
                            <EditText defaultValue={kviz.reviewDurationSec} />
                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} />
                        </Stack>
                    </Stack>
                    <div className="messages" style={{
                        position: 'absolute', left: '50%', top: '10%',
                        transform: 'translate(-50%, -50%)'
                    }}>
                        {errorMessage()}
                        {successMessage()}
                    </div>
                </Stack>
            </Box>
        </div>
    );
};

export default ManageSpecificQuiz;
