import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState, useMemo } from 'react';
import { useNavigate, useParams } from "react-router-dom";
import { Stack } from "@mui/material";
import MyButton from "../components/Buttons/MyButton.js";

const PlayQuiz = () => {

    const { sifKviz } = useParams();
    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [questions, setQuestions] = useState([]);
    const [secondsTimer, setSecondsTimer] = useState('');
    const [duration, setDuration] = useState('');
    const [questionNumber, setQuestionNumber] = useState(0);
    const [numberOfQuestions, setNumberOfQuestions] = useState('');
    const [first, setFirst] = useState(true);
    const [time, setTime] = useState('');



    function Timer() {
        const [timer, setTimer] = useState(0);
        function secondsToTime(secs) {
            let hours = Math.floor(secs / (60 * 60));

            let divisor_for_minutes = secs % (60 * 60);
            let minutes = Math.floor(divisor_for_minutes / 60);

            let divisor_for_seconds = divisor_for_minutes % 60;
            let seconds = Math.ceil(divisor_for_seconds);

            let obj = {
                "h": hours,
                "m": minutes,
                "s": seconds
            };
            return obj;
        }

        function componentDidMount() {
            let timeLeftVar = secondsToTime(secondsTimer);
            setTime(timeLeftVar);
        }

        function startTimer() {
            if (timer == 0 && secondsTimer > 0) {
                setTimer(setTimeout({function(){countDown();}}, 1000));


            }
        }

        const countDown= () => {

            // Remove one second, set state so a re-render happens.
            setSecondsTimer(secondsTimer - 1);
            setTime(secondsToTime(secondsTimer));

            //setSecondsTimer(secondsTimer-1);

            // Check if we're at zero.
            if (secondsTimer == 0) {
                clearInterval(timer);
            }

        };

        startTimer();
        if(time.s<10) {
            return (
                <p style={{ fontSize: "50px" }}>
                    {time.m}:0{time.s}
                </p>
            );
        }
        else{
            return (
                <p style={{ fontSize: "50px" }}>
                    {time.m}:{time.s}
                </p>
            );
        }



    }



    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuestions = async () => {
            let dataa2 = (
                await axios.get('http://localhost:5000/quiz/getQuestions/' + sifKviz + "?number=0")
            ).data;
            //setValue(data[0]);
            setQuestions(dataa2);

            if (first === true) {
                setSecondsTimer(dataa2[0].durationSec);
                setFirst(false);
            }
            setQuestionNumber(0);

        };
        const getKviz = async () => {
            let data2 = (
                await axios.get('http://localhost:5000/quiz/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setNumberOfQuestions(data2.numberOfQuestions);
            if (first === true) {
                setSecondsTimer(data2.durationSec);
                setFirst(false);
            }


        };
        getKviz().catch((err) => console.log(err));
        getQuestions().catch((err) => console.log(err));
        getData().catch((err) => console.log(err));
        setQuestionNumber(0);

    }, [refresh]);

    function getAnotherQuestion() {

        const getQuestions = async () => {
            let dataa2 = (
                await axios.get('http://localhost:5000/quiz/getQuestions/' + sifKviz + "?number=" + (questionNumber + 1))
            ).data;
            //setValue(data[0]);
            setQuestions(dataa2);
        };
        setQuestionNumber(questionNumber + 1);
        getQuestions().catch((err) => console.log(err));
        //renderSearchTable();
    }

    const renderSearchTable = () => {

        return (
            <Stack direction='column' justifyContent='flex-end' alignItems='flex-end' spacing='4rem'  >
                <Stack direction='column' justifyContent='flex-start' alignItems='flex-start' spacing='0rem'  >
                    <p style={{ fontWeight: "bold" }}>Pitanje: {questionNumber + 1}/{numberOfQuestions}</p>
                    <Stack direction='column' justifyContent='flex-start' alignItems='center' spacing='2rem'  >
                        <p style={{ fontSize: "30px" }}>{questions.tekstPitanja}</p>
                        <input style={{ height: "30px", width: "200px" }}></input>
                    </Stack>
                </Stack>
                <MyButton onClick={function () { getAnotherQuestion(); }}>Iduće pitanje</MyButton>
            </Stack>
        );

    };



    return (
        <div>
            <Header />
            <Stack direction='column' justifyContent='center' alignItems='center' spacing='0rem' style={{
                marginBlockStart: '0rem',
            }}>
                <Timer />
                {questions &&
                    < table id="users">
                        <thead>

                        </thead>
                        <tbody>{renderSearchTable()}</tbody>


                    </table>
                }
            </Stack>
        </div>
    );
};

export default PlayQuiz;
