import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { Stack } from "@mui/material";
import MyButton from "../components/Buttons/MyButton.js";
import { useNavigate, useParams } from "react-router-dom";
import { Controller, useForm } from 'react-hook-form';
import Popup from 'reactjs-popup';

const ManageQuestions = () => {

    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [question1, setQuestion1] = useState('');
    const [answer1, setAnswer1] = useState('');
    const [question2, setQuestion2] = useState('');
    const [answer2, setAnswer2] = useState('');
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState('');
    const { handleSubmit } = useForm();
    const { sifKviz } = useParams();
    const [questions, setQuestions] = useState([]);
    const [numberOfSearch, setNumberOfSearch] = useState(10);

    const handleQuestion1 = (e) => {
        setQuestion1(e.target.value);
    };
    const handleNumberOfSearch = () => {
        setNumberOfSearch(numberOfSearch + 5);
    };

    const handleAnswer1 = (e) => {
        setAnswer1(e.target.value);
    };

    const handleQuestion2 = (e) => {
        setQuestion1(e.target.value);
    };

    const handleAnswer2 = (e) => {
        setAnswer1(e.target.value);
    };


    const onSubmitting = async (c, e) => {
        e.preventDefault();
        await axios.post(`http://localhost:5000/admin/quiz/createQuestion1/` + sifKviz + '/' + 1,
            {
                question1,
                answer1
            }

        ).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('lozinku');
            setTimeout(() => { setSuccess(''); }, 3000);
            //setValue(data[0]);
            setQuestionsHelp();

            renderSearchTable();
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => { setError(false); }, 3000);
            setSuccess('');
            return err;
        });


    };

    const setQuestionsHelp = async () => {
        let data2 = (
            await axios.get('http://localhost:5000/admin/quiz/getQuestions/' + sifKviz)
        ).data;
        setQuestions(data2);
        renderSearchTable();
    };

    const successMessage = () => {
        return (
            <div
                className="success"
                style={{
                    display: (success != '') ? '' : 'none',
                }}>
                <p style={{ color: "green", fontSize: 20 }}>Uspješno ste dodali pitanje!</p>
            </div>
        );
    };


    // Showing error message if error is true
    const errorMessage = () => {
        return (
            <div
                className="error"
                style={{
                    display: error ? '' : 'none',
                }}>
                <p style={{ color: "red", fontSize: 20 }}>Niste uspjeli dodati pitanje</p>
            </div>
        );
    };

    const renderSearchTable = () => {
        var i = 0;
        const num = numberOfSearch;
        if (questions) {
            return questions.map(q => {
                while (i < num) {
                    i = i + 1;
                    return (
                        <tr key={q.sifPitanje} style={{ height: '40px', textAlign: 'center' }}>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{i}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid', maxWidth: '500px', overflowX: 'auto' }}>{q.tekstPitanja}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid', borderRight: '1px solid #eeeee4', }}>{q.nazivTip}</td>

                        </tr>
                    );
                }
            });
        }
        else {
            return (
                <p>Ne postoji takav korisnik!</p>
            );
        }

    };

    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuestions = async () => {
            let data2 = (
                await axios.get('http://localhost:5000/admin/quiz/getQuestions/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setQuestions(data2);
        };
        getData().catch((err) => console.log(err));
        getQuestions().catch((err) => console.log(err));
    }, [refresh]);

    return (
        <div>
            <Header />
            <Stack direction='column' justifyContent='center' alignItems='top' spacing='1rem'>
                <h1 style={{ alignSelf: 'center', marginBlockStart: '50px' }}> Upravljaj pitanjima</h1>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem' style={{ marginInline: '200px', alignSelf: 'center' }}>
                    <Popup trigger=
                        {<MyButton className="butonn"> Dodaj novo pitanje samostalnog odgovora </MyButton>}
                    modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '125px 70px',

                                }}>
                                    <div className='content'>
                                        <p>Dodaj novo pitanje slobodnog odgovora!</p>
                                    </div>
                                    <br />
                                    <form onSubmit={handleSubmit(onSubmitting)} >
                                        {/* Labels and inputs for form data */}
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                            <label className="label">Tekst pitanja:  </label>

                                            <input onChange={handleQuestion1} className="input"
                                                type="textarea" name='question1' id="question1" value={question1} placeholder="Koji je glavni grad Meksika?" required />

                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                                            <label className="label">Odgovor:   </label>

                                            <input onChange={handleAnswer1} className="input"
                                                value={answer1} type="textarea" name='answer1' id="answer1" placeholder='Mexico City' required />

                                        </Stack>

                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >
                                            <MyButton type="submit">
                                            Spremi pitanje
                                            </MyButton>
                                            <MyButton onClick=
                                                {() => close()}>
                                                Zatvori
                                            </MyButton>


                                        </Stack>
                                    </form>
                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '20%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>
                    <MyButton>Dodaj novo slagalica pitanje</MyButton>
                    <Popup trigger=
                        {<MyButton className="butonn"> Dodaj novo pitanje sa ponuđenim odgovorima </MyButton>}
                    modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '125px 70px',

                                }}>
                                    <div className='content'>
                                        <p>Dodaj novo pitanje sa ponuđenim odgovorima!</p>
                                    </div>
                                    <br />
                                    <form >
                                        {/* Labels and inputs for form data */}
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                            <label className="label">Tekst pitanja:  </label>

                                            <input onChange={handleQuestion2} className="input"
                                                type="textarea" name='question2' id="question2" value={question2} placeholder="Koje godine je palo Zapadno Rimsko Carstvo?" required />

                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                                            <label className="label">Točan odgovor:   </label>

                                            <input onChange={handleAnswer2} className="input"
                                                value={answer2} type="textarea" name='answer2' id="answer2" placeholder='476. godine' required />

                                        </Stack>
                                        <MyButton type="submit" style={{size:'20px'}}>
                                                Spremi točan odgovor!
                                        </MyButton>

                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >
                                            
                                            <MyButton onClick=
                                                {() => close()}>
                                                Zatvori
                                            </MyButton>


                                        </Stack>
                                    </form>
                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '20%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>
                    <MyButton>Dodaj postojeće pitanje</MyButton>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem' style={{ marginInline: '20px', alignSelf: 'center' }}>
                    {questions &&
                        < table id="users">
                            <thead>
                                <tr style={{
                                    height: '40px',
                                    padding: '12px 0px 12px 0px',
                                    textAlign: 'center',
                                    backgroundColor: 'rgba(255, 165, 0, 0.5)',


                                }}>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Broj</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Tekst pitanja</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Tip pitanja</th>

                                </tr>
                            </thead>
                            <tbody>{renderSearchTable()}</tbody>
                            <MyButton onClick={function () { handleNumberOfSearch(); }}>Show more!</MyButton>
                        </table>
                    }
                </Stack>

            </Stack>
        </div >
    );
};

export default ManageQuestions;
