import axios from 'axios';
import React from 'react';
import { useState } from 'react';
import MyButton from '../MyButton';
import { Stack } from "@mui/material";
import { useNavigate } from 'react-router-dom';

function GetNonAdmins() {
    const url = 'http://localhost:5000/admin/getNonAdmins';
    const navigateTo = useNavigate();
    const [data, setData] = useState([]);
    const [data2, setData2] = useState([]);
    const [numberOfNonAdmins, setNumberOfNonAdmins] = useState(5);
    const [numberOfAdmins, setNumberOfAdmins] = useState(5);
    const [numberOfSearch, setNumberOfSearch] = useState(3);
    const [searchUsernameNonAdmin, setSearchUsernameNonAdmin] = useState('');
    const [searchData, setSearchData] = useState('');

    const handleNumberOfNonAdmins = () => {
        setNumberOfNonAdmins(numberOfNonAdmins + 5);
    };

    const handleSearch = () => {
        setNumberOfSearch(numberOfSearch + 5);
    };

    const handleUsernameNonAdmin = async (e) => {
        setSearchUsernameNonAdmin(e.target.value);
        await axios.get('http://localhost:5000/admin/searchNonAdmins?searchUsernameNonAdmin=' + e.target.value ).then(json => setSearchData(json.data));
        renderSearchTable();
    };

    const handleNumberOfAdmins = () => {
        setNumberOfAdmins(numberOfAdmins + 5);
    };


    React.useEffect(() => {
        axios.get(url).then(json => setData(json.data));
        axios.get('http://localhost:5000/admin/getAdmins').then(json => setData2(json.data));
    }, []);

    const setAdmin = async (e) => {
        await axios.get('http://localhost:5000/admin/setAdmin/' + e);
        await axios.get(url).then(json => setData(json.data));
        await axios.get('http://localhost:5000/admin/getAdmins').then(json => setData2(json.data));
        renderFirstTable();
        renderSecondTable();
    };

    const removeAdmin = async (e) => {
        await axios.get('http://localhost:5000/admin/removeAdmin/' + e);
        await axios.get(url).then(json => setData(json.data));
        await axios.get('http://localhost:5000/admin/getAdmins').then(json => setData2(json.data));
        renderFirstTable();
        renderSecondTable();
    };

    const renderFirstTable = () => {
        var i = 0;
        const num = numberOfNonAdmins;
        return data.map(user => {
            while (i < num) {
                i = i + 1;
                return (
                    <tr key={user.username} style={{ height: '40px', }}>
                        <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.username}</td>
                        <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.name} {user.surname}</td>
                        <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.email}</td>
                        <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ position: 'relative', left: '40%', height: '30px', width: '30px' }} onClick={function () { setAdmin(user.username); }} /></td>
                    </tr>
                );
            }
        });

    };

    const renderSecondTable = () => {
        var i = 0;
        const num = numberOfAdmins;
        return data2.map(user => {
            while (i < num) {
                i = i + 1;
                return (
                    <tr key={user.username} style={{ height: '40px', }}>
                        <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.username}</td>
                        <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.name} {user.surname}</td>
                        <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.email}</td>
                        <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ position: 'relative', left: '40%', height: '30px', width: '30px' }} onClick={function () { removeAdmin(user.username); }} /></td>
                    </tr>
                );
            }
        });

    };

    const renderSearchTable = () => {
        var i = 0;
        const num = numberOfSearch;
        if (searchData) {
            return searchData.map(user => {
                while (i < num) {
                    i = i + 1;
                    return (
                        <tr key={user.username} style={{ height: '40px', }}>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.username}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.name} {user.surname}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{user.email}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ position: 'relative', left: '40%', height: '30px', width: '30px' }} onClick={function () { removeAdmin(user.username); }} /></td>
                        </tr>
                    );
                }
            });
        }
        else {
            return (
                <p>Ne postoji takav korisnik!</p>
            );
        }

    };

    return (
        <div>
            <Stack direction='column' justifyContent='center' alignItems='top' spacing='1rem'>
                <h1 style={{alignSelf:'center', marginBlockStart:'50px'}}> Upravljaj korisnicima</h1>
                <Stack direction='column' justifyContent='center' alignItems='top' spacing='1rem' style={{marginInline:'200px'}}>
                    <form>
                        <p>Pretraži korisnika po imenu</p>
                        <Stack direction='row' justifyContent='left' alignItems='center' spacing='5rem' >
                            <input style={{ height: '30px', width: '300px' }} onChange={handleUsernameNonAdmin} className="input"
                                type="text" name='searchUsernameNonAdmin' id="searchUsernameNonAdmin" value={searchUsernameNonAdmin} placeholder="Upišite ime:" />
                        </Stack>
                    </form>
                    {searchData &&
                        < table id="users">
                            <thead>
                                <tr style={{
                                    height: '40px',
                                    padding: '12px 0px 12px 0px',
                                    textAlign: 'center',
                                    backgroundColor: 'rgba(255, 165, 0, 0.5)',


                                }}>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Korisničko ime</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Ime i prezime</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Email</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Postavi za admina</th>
                                </tr>
                            </thead>
                            <tbody>{renderSearchTable()}</tbody>
                            <MyButton onClick={function () { handleSearch(); }}>Show more!</MyButton>
                        </table>
                    }
                    
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='top' spacing='1rem'>
                    <div>
                        <h1 id="title">Dodaj admina</h1>
                        <p>Svi korisnici koji nisu admini:</p>
                        <table id="users">
                            <thead>
                                <tr style={{
                                    height: '40px',
                                    padding: '12px 0px 12px 0px',
                                    textAlign: 'center',
                                    backgroundColor: 'rgba(255, 165, 0, 0.5)',


                                }}>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Korisničko ime</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Ime i prezime</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Email</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Postavi za admina</th>
                                </tr>
                            </thead>
                            <tbody>{renderFirstTable()}</tbody>
                        </table>
                        <MyButton onClick={function () { handleNumberOfNonAdmins(); }}>Show more!</MyButton>
                    </div>
                    <div>
                        <h1 id="title">Ukloni admina</h1>
                        <table id="users">
                            <thead>
                                <tr style={{
                                    height: '40px',
                                    padding: '12px 0px 12px 0px',
                                    textAlign: 'center',
                                    backgroundColor: 'rgba(255, 165, 0, 0.5)',


                                }}>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Korisničko ime</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Ime i prezime</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Email</th>
                                    <th style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Ukloni admina</th>
                                </tr>
                            </thead>
                            <tbody>{renderSecondTable()}</tbody>
                        </table>
                        <MyButton onClick={function () { handleNumberOfAdmins(); }}>Show more!</MyButton>
                    </div>
                </Stack>
            </Stack>
        </div>
    );
}

export default GetNonAdmins;
