import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { Controller, useForm } from 'react-hook-form';
import BasicDateTimePicker from "../components/DateTimePicker.js";
import ImageUploading from "react-images-uploading";
import { Box, Button, Stack } from "@mui/material";
import MyButton from "../components/Buttons/MyButton.js";
import MySmallButton from "../components/Buttons/MySmallButton.js";

const CreateQuiz = () => {

    const [data, setDataList] = useState([]);
    const [numberOfSearch, setNumberOfSearch] = useState(3);
    const [refresh, setRefresh] = useState(false);
    const [searchData, setSearchData] = useState('');
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [dateAndTimeOpen, setDateAndTimeOpen] = useState(Date.now());
    const [dateAndTimeClose, setDateAndTimeClose] = useState(Date.now());
    const [duration, setDuration] = useState('');
    const [reviewDuration, setReviewDuration] = useState('');
    const [images, setImages] = React.useState();
    const maxNumber = 1;

    const handleName = (e) => {
        setName(e.target.value);
    };
    const handleSearch = () => {
        setNumberOfSearch(numberOfSearch + 5);
    };

    const handleUsernameNonAdmin = async (e) => {
        setSearchUsernameNonAdmin(e.target.value);
        await axios.get('http://localhost:5000/admin/searchNonAdmins?searchUsernameNonAdmin=' + e.target.value ).then(json => setSearchData(json.data));
        renderSearchTable();
    };

    const handleDescription = (e) => {
        setDescription(e.target.value);
    };
    const handleDuration = (e) => {
        setDuration(e.target.value);
    };
    const handleReviewDuration = (e) => {
        setReviewDuration(e.target.value);
    };

    function UploadImage() {
        const onChange = (imageList, addUpdateIndex) => {
            // data for submit
            console.log(imageList, addUpdateIndex);
            setImages(imageList);
        };

        return (
            <div className="App">
                <ImageUploading
                    multiple
                    value={images}
                    onChange={onChange}
                    maxNumber={maxNumber}
                    dataURLKey="data_url"
                    acceptType={["jpg"]}
                >
                    {({
                        imageList,
                        onImageUpload,
                        onImageRemoveAll,
                        onImageUpdate,
                        onImageRemove,
                        isDragging,
                        dragProps
                    }) => (
                        // write your building UI
                        <div className="upload__image-wrapper">
                            {!images &&
                                <MyButton
                                    style={isDragging ? { color: "red" } : null}
                                    onClick={onImageUpload}
                                    {...dragProps}
                                >
                                    Slika
                                </MyButton>
                            }
                            &nbsp;
                            {imageList.map((image, index) => (
                                <div key={index} className="image-item">
                                    <Stack direction='column' justifyContent='space-between' alignItems='center' spacing='1rem'>
                                        <img src={image.data_url} alt="" width="100" />
                                        <div className="image-item__btn-wrapper">

                                            <MySmallButton onClick={() => onImageUpdate(index)}>Update</MySmallButton>



                                        </div>
                                    </Stack>
                                </div>
                            ))}
                        </div>
                    )}
                </ImageUploading>
            </div>
        );
    }

    const renderSearchTable = () => {
        var i = 0;
        const num = numberOfSearch;
        if (searchData) {
            return searchData.map(c => {
                while (i < num) {
                    i = i + 1;
                    return (
                        <tr key={user.username} style={{ height: '40px', }}>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{c.nazivKategorije}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><MyButton style={{ position: 'relative', left: '40%', height: '30px', width: '30px' }}>Odaberi</MyButton></td>
                            
                        </tr>
                    );
                }
            });
        }
        else {
            return (
                <p>Ne postoji takav korisnik!</p>
            );
        }

    };

    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        getData().catch((err) => console.log(err));
    }, [refresh]);

    return (
        <div>
            <Header />
            <div className='form'>
                <Stack direction='column' justifyContent='space-between' alignItems='center' spacing='1rem'
                >
                    <br />
                    <h2 style={{ position: 'relative' }}> Kreiraj novi kviz</h2>

                    {/* Calling to the methods */}
                    <form style={{
                        width: "500px",
                        border: '10px solid rgba(255, 165, 0, 0.5)',
                        padding: '20px'
                    }}>
                        {/* Labels and inputs for form data */}
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='1rem' >
                            <label className="label">Naziv kviza  </label>

                            <input onChange={handleName} className="input"
                                type="text" name='name' id="name" value={name} placeholder="Zastave Europe" style={{ height: '30px', width: '200px' }} required />
                        </Stack>
                        <br />
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                            <label className="label">Opis kviza:  </label>

                            <textarea onChange={handleDescription} className="input"
                                type="textarea" name='description' id="description" value={description} placeholder="Vrlo poučan i edukativan kviz za sve uzraste" style={{ height: '50px', width: '200px' }} required />
                        </Stack>
                        <br />
                        <Stack direction='column' justifyContent='center' alignItems='top' spacing='1rem' style={{ marginInline: '200px' }}>
                                <p>Pretraži korisnika po imenu</p>
                                <Stack direction='row' justifyContent='left' alignItems='center' spacing='5rem' >
                                    <input style={{ height: '30px', width: '300px' }} onChange={handleUsernameNonAdmin} className="input"
                                        type="text" name='searchUsernameNonAdmin' id="searchUsernameNonAdmin" value={searchUsernameNonAdmin} placeholder="Upišite ime:" />
                                </Stack>
                            {searchData &&
                                < table id="users">
                                    <thead>
                                        <tr style={{
                                            height: '40px',
                                            padding: '12px 0px 12px 0px',
                                            textAlign: 'center',
                                            backgroundColor: 'rgba(255, 165, 0, 0.5)',


                                        }}>
                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Korisničko ime</th>
                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Ime i prezime</th>
                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Email</th>
                                            <th style={{ borderLeft: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Admin</th>
                                            <th style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlock: '1px solid', margin: '0px' }}>Postavi ili ukloni admina</th>
                                        </tr>
                                    </thead>
                                    <tbody>{renderSearchTable()}</tbody>
                                    <MyButton onClick={function () { handleSearch(); }}>Show more!</MyButton>
                                </table>
                            }

                        </Stack>
                        <br />
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                            <label className="label">Broj sekundi trajanja:   </label>

                            <input onChange={handleDuration} className="input"
                                value={duration} type="text" name='duration' id="duration" placeholder='60' style={{ height: '30px', width: '200px' }} required />
                        </Stack>

                        <br />

                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='4.2rem' >
                            <label className="label">Broj sekundi trajanja pregleda:</label>
                            <input onChange={handleReviewDuration} className="input"
                                value={reviewDuration} type="text" name='reviewDuration' id="reviewDuration" placeholder='100' style={{ height: '30px', width: '200px' }} required />

                        </Stack>
                        <br />
                        <Stack direction='column' justifyContent='space-between' alignItems='center' spacing='0.5rem' >
                            <label className="label">Datum i vrijeme otvaranja kviza</label>
                            <BasicDateTimePicker
                                value={dateAndTimeOpen}
                                onChange={(newValue) => setDateAndTimeOpen(newValue)}
                                name='dateAndTimeOpen'

                                required
                            />
                        </Stack>
                        <br />
                        <Stack direction='column' justifyContent='space-between' alignItems='center' spacing='0.5rem' >
                            <label className="label">Datum i vrijeme zatvaranja kviza</label>
                            <BasicDateTimePicker
                                value={dateAndTimeClose}
                                onChange={(newValue) => setDateAndTimeClose(newValue)}
                                name='dateAndTimeClose'
                                required
                            />
                        </Stack>
                        <br />
                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='4.2rem' >
                            <label className="label">Učitajte sliku</label>
                            <br />
                            <UploadImage />
                        </Stack>
                        <br />
                        <MyButton className="btn" type="submit">
                            Submit
                        </MyButton>

                        <br />
                    </form>

                </Stack>
                <div className="messages" style={{
                    position: 'absolute', left: '50%', top: '20%',
                    transform: 'translate(-50%, -50%)'
                }}>

                </div>
            </div>
        </div>
    );
};

export default CreateQuiz;
