import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { Stack } from "@mui/material";
import { useNavigate, useParams } from "react-router-dom";

const MainPage = () => {

    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const { sifKviz } = useParams();
    const [kviz, setKviz] = useState('');
    const [date, setDate] = useState('');


    function formatDate(string){
        var options = { year: 'numeric', month: 'long', day: 'numeric' };
        return new Date(string).toLocaleDateString([],options);
    }
    
    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getKviz = async () => {
            let data2 = (
                await axios.get('http://localhost:5000/quiz/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setKviz(data2);
            setDate(formatDate(data2.availableTo));
        };
        getKviz().catch((err) => console.log(err));
        getData().catch((err) => console.log(err));
    }, [refresh]);

    return (
        <div>
            <Header />
            <Stack direction='row' justifyContent='center' alignItems='center' spacing='5rem' style={{
                marginBlock: '4rem',
            }}>
                <div ><img src={kviz.image} alt="" style={{ height: '350px' }} /></div>
                <Stack direction='column' justifyContent='flex-start' alignItems='flex-start' spacing='1rem' style={{ width: "300px", textAlign: "left" }}>
                    <p style={{ fontSize: "40px", fontWeight: 'bold', width:"50rem"}}>{kviz.name} </p>
                    <p style={{ fontSize: "20px", fontStyle: 'italic', width:"30rem"}}>{kviz.description}</p>
                    <p style={{ fontSize: "17px"}}>Broj pitanja: {kviz.numberOfQuestions}</p>
                    <p style={{ fontSize: "17px"}}>Zatvara se u: {date}</p>
                    <p style={{ fontSize: "17px", fontWeight: 'bold' }}>Kategorija: {kviz.nazivKategorije}</p>
                </Stack>
            </Stack>
        </div>
            
    );
};

export default MainPage;
