import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { useNavigate, useParams } from "react-router-dom";
import { Stack } from "@mui/material";

const PlayQuiz = () => {

    const { sifKviz } = useParams();
    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [questions, setQuestions] = useState([]);
    const [secondsTimer, setSecondsTimer] = useState([]);
    const [duration, setDuration] = useState('');

    function Countdown() {
        var date = new Date(0);
        date.setSeconds(secondsTimer); // specify value for SECONDS here
        setDuration(date.toISOString().substring(14, 19));
        
    }

    function Timer() {
        while(secondsTimer!==0){
            setTimeout(() => { setSecondsTimer(secondsTimer - 1); Countdown(); }, 500);
            return(
                <p style={{fontSize:"50px"}}>{duration}</p>
            );
            
        }
        
    }

    
    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuestions = async () => {
            let dataa2 = (
                await axios.get('http://localhost:5000/quiz/getQuestions/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setQuestions(dataa2);
            setSecondsTimer(dataa2[0].durationSec);
        };
        getQuestions().catch((err) => console.log(err));
        getData().catch((err) => console.log(err));
    }, [refresh]);

    

    return(
        <div>
            <Header />
            <Stack direction='column' justifyContent='center' alignItems='center' spacing='3rem' style={{
                marginBlockStart: '4rem',
            }}>
                <Timer/>
            </Stack>
        </div>
    );
};

export default PlayQuiz;
