import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { useNavigate, useParams } from "react-router-dom";
import { Stack } from "@mui/material";

const PlayQuiz = () => {

    const { sifKviz } = useParams();
    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [questions, setQuestions] = useState([]);
    const [secondsTimer, setSecondsTimer] = useState('');
    const [duration, setDuration] = useState('');
    const [ret, setRet] = useState('');
    const [counter, setCounter] = React.useState(120);

    const padTime = time => {
        return String(time).length === 1 ? `0${time}` : `${time}`;
    };

    const format = time => {
        // Convert seconds into minutes and take the whole part
        const minutes = Math.floor(time / 60);

        // Get the seconds left after converting minutes
        const seconds = time % 60;

        //Return combined values as string in format mm:ss
        return `${minutes}:${padTime(seconds)}`;
    };




    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuestions = async () => {
            let dataa2 = (
                await axios.get('http://localhost:5000/quiz/getQuestions/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setQuestions(dataa2);
            setSecondsTimer(dataa2[0].durationSec);

        };
        getQuestions().catch((err) => console.log(err));
        getData().catch((err) => console.log(err));
        let timer;
        if (counter > 0) {
            timer = setTimeout(() => setCounter(c => c - 1), 1000);
        }

        
    }, [counter]);



    return (
        <div>
            <Header />
            <Stack direction='column' justifyContent='center' alignItems='center' spacing='3rem' style={{
                marginBlockStart: '4rem',
            }}>
                <div className="App">
                    {counter === 0 ? "Time over" : <div>{format(counter)}</div>}
                </div>

            </Stack>
        </div>
    );
};

export default PlayQuiz;
