import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { useNavigate, useParams } from "react-router-dom";

const PlayQuiz = () => {

    const { sifKviz } = useParams();
    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [questions, setQuestions] = useState([]);
    const [seconds, setSeconds] = useState([]);
    const [duration, setDuration] = useState([]);

    function Countdown() {
        var date = new Date(0);
        date.setSeconds(seconds); // specify value for SECONDS here
        setDuration(date.toISOString().substring(14, 19));
    }

    function Timer() {
        while(seconds!==0){
            setTimeout(() => { setSeconds(seconds - 1); setSeconds(seconds); Countdown(); }, 1000);
            return(
                <p>{duration}</p>
            );
        }
        
    }

    
    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuestions = async () => {
            let dataa2 = (
                await axios.get('http://localhost:5000/quiz/getQuestions/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setQuestions(dataa2);
            setSeconds(dataa2[0].durationSec);
        };
        getQuestions().catch((err) => console.log(err));
        getData().catch((err) => console.log(err));
    }, [refresh]);

    

    return(
        <div>
            <Header />
            
        </div>
    );
};

export default PlayQuiz;
