import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { useNavigate, useParams } from "react-router-dom";
import { Stack } from "@mui/material";

const PlayQuiz = () => {

    const { sifKviz } = useParams();
    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [questions, setQuestions] = useState([]);
    const [secondsTimer, setSecondsTimer] = useState('');
    const [duration, setDuration] = useState('');


    class Timer extends React.Component {
        constructor() {
          super();
          this.state = { time: {}, seconds: 5 };
          this.timer = 0;
          this.startTimer = this.startTimer.bind(this);
          this.countDown = this.countDown.bind(this);
        }
      
        secondsToTime(secs){
          let hours = Math.floor(secs / (60 * 60));
      
          let divisor_for_minutes = secs % (60 * 60);
          let minutes = Math.floor(divisor_for_minutes / 60);
      
          let divisor_for_seconds = divisor_for_minutes % 60;
          let seconds = Math.ceil(divisor_for_seconds);
      
          let obj = {
            "h": hours,
            "m": minutes,
            "s": seconds
          };
          return obj;
        }
      
        componentDidMount() {
          let timeLeftVar = this.secondsToTime(this.state.seconds);
          this.setState({ time: timeLeftVar });
        }
      
        startTimer() {
          if (this.timer == 0 && this.state.seconds > 0) {
            this.timer = setInterval(this.countDown, 1000);
          }
        }
      
        countDown() {
          // Remove one second, set state so a re-render happens.
          let seconds = this.state.seconds - 1;
          this.setState({
            time: this.secondsToTime(seconds),
            seconds: seconds,
          });
          
          // Check if we're at zero.
          if (seconds == 0) { 
            clearInterval(this.timer);
          }
        }
      
        render() {
          return(
            <div>
              <button onClick={this.startTimer}>Start</button>
              m: {this.state.time.m} s: {this.state.time.s}
            </div>
          );
        }
      }
      


    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuestions = async () => {
            let dataa2 = (
                await axios.get('http://localhost:5000/quiz/getQuestions/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setQuestions(dataa2);
            setSecondsTimer(dataa2[0].durationSec);

        };
        getQuestions().catch((err) => console.log(err));
        getData().catch((err) => console.log(err));
        
    }, [refresh]);



    return (
        <div>
            <Header />
            <Stack direction='column' justifyContent='center' alignItems='center' spacing='3rem' style={{
                marginBlockStart: '4rem',
            }}>
                <Timer/>
            </Stack>
        </div>
    );
};

export default PlayQuiz;
