import React from "react";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import Popup from 'reactjs-popup';
import { Box, Button, Stack } from "@mui/material";
import MyButton from "../components/MyButton.js";
import { useNavigate } from 'react-router-dom';

const ProfilePage = () => {
    const navigateTo = useNavigate();
    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    let uloga = 'Korisnik';
    const [name, setName] = useState('');
    const [surname, setSurname] = useState('');
    const [email, setEmail] = useState('');
    const [pbr, setPbr] = useState('');
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState('');

    const handleChange = (e) => {
        setPbr(e.target.value);
    };
    const handleName = (e) => {
        setName(e.target.value);

    };
    const handleSurname = (e) => {
        setSurname(e.target.value);
    };

    // Handling the email change
    const handleEmail = (e) => {
        setEmail(e.target.value);

    };

    const changeName = async (e) => {
        await axios.post(`http://localhost:5000/changeName`,
            {
                name,
            }).then((response) => {
                console.log(response.json);
                setError(false);
                setSuccess('ime');
                return response.data;
            }).catch((err) => {
                setError(true);
                setSuccess('');
                return err;
            });
    };

    const changeSurname = async (e) => {
        await axios.post(`http://localhost:5000/changeSurname`,
            {
                surname,
            }).then((response) => {
                console.log(response.json);
                setError(false);
                setSuccess('prezime');
                return response.data;
            }).catch((err) => {
                setError(true);
                setSuccess('');
                return err;
            });
    };

    const changeEmail = async (e) => {
        await axios.post(`http://localhost:5000/changeEmail`,
            {
                email,
            }).then((response) => {
                console.log(response.json);
                setError(false);
                setSuccess('email');
                return response.data;
        }).catch((err) => {
            setError(true);
            setSuccess('');
            return err;
        });
    };

    const changePbr = async (e) => {
        await axios.post(`http://localhost:5000/changePbr`,
            {
                pbr,
            }).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('mjesto stanovanja');
            return response.data;
        }).catch((err) => {
            setError(true);
            setSuccess('');
            return err;
        });
    };


    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
            if (data.admin == 1) {
                uloga = 'Admin';
            }
        };
        getData().catch((err) => console.log(err));
    }, [refresh]);

    const successMessage = () => {
        return (
            <div
                className="success"
                style={{
                    display: (success!= '') ? '' : 'none',
                }}>
                <h1> Uspješno ste promjenili {success}!</h1>
            </div>
        );
    };


    // Showing error message if error is true
    const errorMessage = () => {
        return (
            <div
                className="error"
                style={{
                    display: error ? '' : 'none',
                }}>
                <p style={{ color: "red", fontSize: 20 }}>Vaša promjena nije uspjela</p>
            </div>
        );
    };


    //if (!data) return <Navigate to='/' />;
    return (
        <div>
            <Header />
            <Box
                sx={{
                    display: 'flex',
                    alignItems: 'flex-start',
                    flexDirection: 'column',
                    p: 1,
                    m: 1,
                    borderRadius: 7,
                    border: '6px solid rgba(255, 165, 0, 0.5)',
                    position: 'absolute', left: '33%', top: '15%',
                    padding: '30px 30px 30px 30px',
                    borderColor: 'orange'
                }}>

                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <p> Korisničko ime: {data.username}</p>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <p>Ime: {data.name} </p>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <p>Prezime: {data.surname} </p>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <p>Email adresa: {data.email}</p>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <p>Uloga: {uloga}</p>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <p>Grad: </p>
                </Stack>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem'>
                    <Popup trigger=
                        {<MyButton className="butonn"> Uredi Podatke </MyButton>}
                    modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '70px',

                                }}>
                                    <div className='content'>
                                        Ispunite polja koja želite mjenjati, te stisnite zelenu kvačicu
                                        <br />
                                        pored njih. Pri završetku stisnite gumb <h style={{ color: 'orange' }}>Gotovo</h>,


                                        <br />
                                        te osvježite stranicu da vam se učitaju promjene
                                    </div>
                                    <br />
                                    <form>
                                        {/* Labels and inputs for form data */}
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                            <label className="label">Ime:  </label>

                                            <input onChange={handleName} className="input"
                                                type="text" name='name' id="name" value={name} placeholder="Name" required />
                                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={changeName} />
                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                                            <label className="label">Prezime:   </label>

                                            <input onChange={handleSurname} className="input"
                                                value={surname} type="text" name='surname' id="surname" placeholder='Last name' required />
                                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={changeSurname} />
                                        </Stack>

                                        <br />

                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >
                                            <label className="label">Email:</label>
                                            <input onChange={handleEmail} className="input"
                                                value={email} type="email" name='email' id="email" placeholder='Email' required />
                                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={changeEmail} />
                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='flex-start' alignItems='center' spacing='11.4rem' >
                                            <label className="label">Grad</label>
                                            <select name='pbr' title='pbr' id="pbr" value={pbr} onChange={handleChange}>
                                                <option value="12321">12321</option>
                                                <option value="12343">12343</option>
                                            </select>
                                            <img src={'https://jf-staeulalia.pt/img/other/30/collection-green-check-mark-6.jpg'} style={{ height: '30px', width: '30px' }} onClick={changePbr} />
                                        </Stack>
                                        <br />
                                    </form>
                                    <div>
                                        <MyButton onClick=
                                            {() => close()}>
                                            Gotovo
                                        </MyButton>
                                    </div>
                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '20%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>

                    <MyButton>Promijeni lozinku</MyButton>
                </Stack>




            </Box>

        </div>
    );
};

export default ProfilePage;
