import React from "react";
import Login from "../components/Login/Login.js";
import Header from "../components/Header/Header.js";
import axios from 'axios';
import { useState } from 'react';
import { Navigate } from 'react-router-dom';
import { Stack } from "@mui/material";
import MyButton from "../components/Buttons/MyButton.js";
import { useNavigate, useParams } from "react-router-dom";
import { Controller, useForm } from 'react-hook-form';
import Popup from 'reactjs-popup';

const ManageQuestions = () => {

    const [data, setDataList] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const [question1, setQuestion1] = useState('');
    const [answer1, setAnswer1] = useState('');
    const [error, setError] = useState(false);
    const [success, setSuccess] = useState('');
    const { handleSubmit } = useForm();
    const { sifKviz } = useParams();
    const [questions, setQuestions] = useState([]);
    const [numberOfSearch, setNumberOfSearch] = useState(10);

    const handleQuestion1 = (e) => {
        setQuestion1(e.target.value);
    };

    const handleAnswer1 = (e) => {
        setAnswer1(e.target.value);
    };

    const onSubmitting = async (c, e) => {
        e.preventDefault();
        await axios.post(`http://localhost:5000/admin/quiz/createQuestion1/` + sifKviz + '/' + 1,
            {
                question1,
                answer1
            }

        ).then((response) => {
            console.log(response.json);
            setError(false);
            setSuccess('lozinku');
            setTimeout(() => { setSuccess(''); }, 3000);
            return response.data;
        }).catch((err) => {
            setError(true);
            setTimeout(() => { setError(false); }, 3000);
            setSuccess('');
            return err;
        });

    };

    const successMessage = () => {
        return (
            <div
                className="success"
                style={{
                    display: (success != '') ? '' : 'none',
                }}>
                <p style={{ color: "green", fontSize: 20 }}>Uspješno ste dodali pitanje!</p>
            </div>
        );
    };


    // Showing error message if error is true
    const errorMessage = () => {
        return (
            <div
                className="error"
                style={{
                    display: error ? '' : 'none',
                }}>
                <p style={{ color: "red", fontSize: 20 }}>Niste uspjeli dodati pitanje</p>
            </div>
        );
    };

    const renderSearchTable = () => {
        var i = 0;
        const num = numberOfSearch;
        if (questions) {
            return questions.map(kviz => {
                while (i < num) {
                    i = i + 1;
                    return (
                        <tr key={kviz.sifKviz} style={{ height: '40px', textAlign: 'center' }}>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={kviz.image} alt="" width="100" /></td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.name}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid', maxWidth: '300px', overflowX: 'auto' }}>{kviz.description}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.nazivKategorije}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.availableFrom}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.availableTo}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.durationSec}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}>{kviz.reviewDurationSec}</td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src= {'https://www.vagaservis.hr/images/upitnik.jpg'} style= {{height: '50px'}}onClick={function () { navigateTo('/admin/manageQuestions/' + kviz.sifKviz); }}></img></td>
                            <td style={{ borderLeft: '1px solid #eeeee4', borderRight: '1px solid #eeeee4', borderBlockEnd: '1px solid' }}><img src={'https://cdn.dribbble.com/users/3293/screenshots/2591498/screen_shot_2016-03-15_at_12.51.52_pm.png'} style={{ position: 'relative', height: '50px', width: '50px' }} onClick={function () { navigateTo('/admin/manageQuizes/' + kviz.sifKviz); }} /></td>

                        </tr>
                    );
                }
            });
        }
        else {
            return (
                <p>Ne postoji takav korisnik!</p>
            );
        }

    };

    React.useEffect(() => {
        const getData = async () => {
            let dataa = (
                await axios.get('http://localhost:5000/session')
            ).data;
            //setValue(data[0]);
            setDataList(dataa);
        };
        const getQuestions = async () => {
            let data2 = (
                await axios.get('http://localhost:5000/admin/quiz/getQuestions/' + sifKviz)
            ).data;
            //setValue(data[0]);
            setQuestions(data2);
        };
        getData().catch((err) => console.log(err));
        getQuestions().catch((err) => console.log(err));
    }, [refresh]);

    return (
        <div>
            <Header />
            <Stack direction='column' justifyContent='center' alignItems='top' spacing='1rem'>
                <h1 style={{ alignSelf: 'center', marginBlockStart: '50px' }}> Upravljaj pitanjima</h1>
                <Stack direction='row' justifyContent='center' alignItems='center' spacing='1rem' style={{ marginInline: '200px', alignSelf: 'center' }}>
                    <Popup trigger=
                        {<MyButton className="butonn"> Dodaj novo pitanje samostalnog odgovora </MyButton>}
                    modal nested>
                        {
                            close => (
                                <div className='modal' style={{
                                    backgroundColor: "white", borderRadius: 7,
                                    border: '6px solid rgba(255, 165, 0, 0.5)',
                                    padding: '125px 70px',

                                }}>
                                    <div className='content'>

                                    </div>
                                    <br />
                                    <form onSubmit={handleSubmit(onSubmitting)}>
                                        {/* Labels and inputs for form data */}
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='5rem' >
                                            <label className="label">Tekst pitanja:  </label>

                                            <input onChange={handleQuestion1} className="input"
                                                type="textarea" name='question1' id="question1" value={question1} placeholder="Koji je glavni grad Meksika?" required />

                                        </Stack>
                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='3rem'>
                                            <label className="label">Odgovor:   </label>

                                            <input onChange={handleAnswer1} className="input"
                                                value={answer1} type="textarea" name='answer1' id="answer1" placeholder='Mexico City' required />

                                        </Stack>


                                        <br />
                                        <Stack direction='row' justifyContent='space-between' alignItems='center' spacing='2rem' >
                                            <MyButton type="submit">
                                                Spremi pitanje
                                            </MyButton>
                                            <MyButton onClick=
                                                {() => close()}>
                                                Zatvori
                                            </MyButton>

                                            
                                        </Stack>
                                    </form>
                                    <div className="messages" style={{
                                        position: 'absolute', left: '50%', top: '20%',
                                        transform: 'translate(-50%, -50%)'
                                    }}>
                                        {errorMessage()}
                                        {successMessage()}
                                    </div>
                                </div>
                            )
                        }
                    </Popup>
                    <MyButton>Dodaj novo slagalica pitanje</MyButton>
                    <MyButton>Dodaj novo pitanje s ponuđenim odgovorima</MyButton>
                    <MyButton>Dodaj postojeće pitanje</MyButton>
                </Stack>

            </Stack>
        </div >
    );
};

export default ManageQuestions;
